con:config.script:
import jxl.*

Workbook workbook = Workbook.getWorkbook(new File("C:\\Users\\temo\\Desktop\\TestData.xls"))
Sheet sheet1 = workbook.getSheet("Sheet1")
def rows = sheet1.getRows()
def cols = sheet1.getColumns()
def array = []
for(i=1;i<rows;i++) { 
    for(j=0;j<cols;j++) { 
       Cell cell = sheet1.getCell(j,i)
       def variable = cell.getContents()
       //log.info cell.getContents() 
       array << variable
    } 
}
testRunner.testCase.setPropertyValue("intA", array[0].toString())
testRunner.testCase.setPropertyValue("intB", array[1].toString())
def result = array[0].toInteger() + array[1].toInteger()
//log.info result
testRunner.testCase.setPropertyValue("ExpectedResult", result.toString())

